mapper.select:
select 

from sh_video_detail
where type = #{type,jdbcType=VARCHAR}
  and sh_video_base_id = #{videoBaseId,jdbcType=INTEGER}

mapper.delete:
delete from sh_video_detail
where type = #{type,jdbcType=VARCHAR}
  and sh_video_base_id = #{videoBaseId,jdbcType=INTEGER}

mapper.insert:
insert into sh_video_detail (type, sh_video_base_id, url, 
  updatetime, store_info, store_type
  )
values (#{type,jdbcType=VARCHAR}, #{videoBaseId,jdbcType=INTEGER}, #{url,jdbcType=VARCHAR}, 
  #{updatetime,jdbcType=TIMESTAMP}, #{storeInfo,jdbcType=VARCHAR}, #{storeType,jdbcType=VARCHAR}
  )

mapper.update:
update sh_video_detail
set url = #{url,jdbcType=VARCHAR},
  updatetime = #{updatetime,jdbcType=TIMESTAMP},
  store_info = #{storeInfo,jdbcType=VARCHAR},
  store_type = #{storeType,jdbcType=VARCHAR}
where type = #{type,jdbcType=VARCHAR}
  and sh_video_base_id = #{videoBaseId,jdbcType=INTEGER}
