mapper.select:
SELECT past.id videoPastpaperId, base.question_id, base.id, base.title, base.desc, base.difficulty, base.status, base.question_number, base.profile, base.updatetime, bank.id bankid, bank.name bankname, course.id courseId, course.name courseName, bt.id btid, bt.name btname, year.id yearid, year.year year, qtype.id typeId, qtype.name typeName, lecture.id lectureId, lecture.name lectureName, lecture.level lectureLevel, group_concat(sk.name) keyword, cast(group_concat(sk.id) AS CHAR) keywordId, group_concat(kp.name) knowledge, cast(group_concat(kp.id) AS CHAR) knowledgeId
FROM sh_video_base base INNER JOIN sh_video_pastpaper past ON past.sh_video_base_id = base.id LEFT JOIN sh_questionbank bank ON bank.id = past.sh_questionbank_id LEFT JOIN sh_course course ON course.id = base.sh_course_id LEFT JOIN sh_questionbank_type bt ON bt.id = past.sh_questionbank_type_id LEFT JOIN sh_questionbank_year year ON year.id = past.sh_questionbank_year_id LEFT JOIN sh_question_type qtype ON qtype.id = past.sh_question_type_id LEFT JOIN sh_lecture lecture ON lecture.id = base.sh_lecture_id left join sh_video_base_keyword kw on kw.sh_video_base_id = base.id left join sh_keyword sk on sk.id = kw.sh_keyword_id
left join sh_video_base_knowledge_point pt on pt.sh_video_base_id = base.id left join sh_knowledge_point kp on kp.id = pt.sh_knowledge_point_id
WHERE 1=1
and base.id = #{id}
and past.sh_questionbank_id = #{questionBankId}
and base.sh_course_id = #{courseId}
and past.sh_questionbank_type_id = #{questionBankTypeId}
and past.sh_questionbank_year_id = #{questionBankYearId}
and past.sh_question_type_id = #{questionTypeId}
and base.sh_lecture_id = #{lectureId}
and base.title like '%${questionDesc}%'
and base.difficulty = #{difficulty}
and exists (select 1 from sh_video_detail detail where detail.sh_video_base_id = base.id)
and kw.sh_keyword_id in
${item}
and pt.sh_knowledge_point_id in
${item}
group by id
