mapper.select:
SELECT a_tab.id videoPastpaperId, b_tab.question_id, b_tab.title, b_tab.desc, b_tab.courseId, b_tab.courseName, b_tab.courseShortName, b_tab.question_number, b_tab.knowledgePointName, b_tab.knowledgePointId, b_tab.keywordName, b_tab.keywordId, b_tab.videoDetailUrl, b_tab.lectureId, b_tab.lectureName, b_tab.lectureLevel, b_tab.difficulty
FROM (SELECT a_tab.id, a_tab.sh_video_base_id FROM sh_video_point a_tab LIMIT 0 , 20)a_tab
left join (
SELECT b_tab.id, b_tab.question_number, b_tab.question_id, b_tab.title, b_tab.desc, b_tab.sh_admin_user_id, b_tab.sh_course_id, b_tab.sh_lecture_id, a_tab.id courseId, a_tab.name courseName, a_tab.short courseShortName, c_tab.name lectureName, c_tab.level lectureLevel, c_tab.id lectureId, d_tab.knowledgePointName, d_tab.knowledgePointId, e_tab.keywordName, e_tab.keywordId, b_tab.difficulty, f_tab.url videoDetailUrl FROM sh_video_base b_tab
left join sh_course a_tab on b_tab.sh_course_id = a_tab.id left join sh_lecture c_tab on c_tab.id = b_tab.sh_lecture_id left join ( select a_tab.sh_video_base_id,b_tab.name knowledgePointName,b_tab.id knowledgePointId from sh_video_base_knowledge_point a_tab left join sh_knowledge_point b_tab on a_tab.sh_knowledge_point_id=b_tab.id )d_tab on d_tab.sh_video_base_id = b_tab.id left join ( select a_tab.sh_video_base_id,b_tab.name keywordName,b_tab.id keywordId from sh_video_base_keyword a_tab left join sh_keyword b_tab on a_tab.sh_keyword_id=b_tab.id )e_tab on e_tab.sh_video_base_id = b_tab.id left join sh_video_detail f_tab on b_tab.id = f_tab.sh_video_base_id
) b_tab on a_tab.sh_video_base_id = b_tab.id
where 1=1
and a_tab.id = #{id}
and b_tab.courseId = #{videoBase.course.id}
and b_tab.difficulty = #{videoBase.difficulty}
and b_tab.videoDetailUrl is null
and b_tab.videoDetailUrl is not null
and b_tab.keywordName in
#{keyword.name}
and b_tab.knowledgePointName in
#{knowledgetPoint.name}
